source.data:
// uniform
uniform sampler2D inData;

// output
out vec4 outData;

source.mainBody:
				float FXAA_SPAN_MAX = 8.0;
				float FXAA_REDUCE_MUL = 1.0/8.0;
				float FXAA_REDUCE_MIN = (1.0/128.0);

				vec3 rgbNW = texture(inData, varUV + (vec2(-1.0, -1.0) * inOutputTexelSize)).rgb;
				vec3 rgbNE = texture(inData, varUV + (vec2(+1.0, -1.0) * inOutputTexelSize)).rgb;
				vec3 rgbSW = texture(inData, varUV + (vec2(-1.0, +1.0) * inOutputTexelSize)).rgb;
				vec3 rgbSE = texture(inData, varUV + (vec2(+1.0, +1.0) * inOutputTexelSize)).rgb;
				vec3 rgbM  = texture(inData, varUV).rgb;

				vec3 luma = vec3(0.299, 0.587, 0.114);
				float lumaNW = dot(rgbNW, luma);
				float lumaNE = dot(rgbNE, luma);
				float lumaSW = dot(rgbSW, luma);
				float lumaSE = dot(rgbSE, luma);
				float lumaM  = dot(rgbM,  luma);

				float lumaMin = min(lumaM, min(min(lumaNW, lumaNE), min(lumaSW, lumaSE)));
				float lumaMax = max(lumaM, max(max(lumaNW, lumaNE), max(lumaSW, lumaSE)));

				vec2 dir;
				dir.x = -((lumaNW + lumaNE) - (lumaSW + lumaSE));
				dir.y =  ((lumaNW + lumaSW) - (lumaNE + lumaSE));

				float dirReduce = max((lumaNW + lumaNE + lumaSW + lumaSE) * (0.25 * FXAA_REDUCE_MUL), FXAA_REDUCE_MIN);
				  
				float rcpDirMin = 1.0/(min(abs(dir.x), abs(dir.y)) + dirReduce);

				dir = min(vec2(FXAA_SPAN_MAX,  FXAA_SPAN_MAX), 
					  max(vec2(-FXAA_SPAN_MAX, -FXAA_SPAN_MAX), dir * rcpDirMin)) * inOutputTexelSize;
					
				vec3 rgbA = (1.0/2.0) * (
						  texture(inData, varUV + dir * (1.0/3.0 - 0.5)).rgb +
						  texture(inData, varUV + dir * (2.0/3.0 - 0.5)).rgb);
				vec3 rgbB = rgbA * (1.0/2.0) + (1.0/4.0) * (
						  texture(inData, varUV + dir * (0.0/3.0 - 0.5)).rgb +
						  texture(inData, varUV + dir * (3.0/3.0 - 0.5)).rgb);
				float lumaB = dot(rgbB, luma);

				if((lumaB < lumaMin) || (lumaB > lumaMax)){
					outData.rgb = rgbA;
				} else {
					outData.rgb = rgbB;
				}
				outData.a = 1.0;
